import React from "react";
import { useState } from "react";

import "./index.scss";
import slideData from "../../config/slideData";
import { useEffect } from "react";
import imgData from "../../config/slideData";
import { useRef } from "react";

export default function SlideList() {
  const [slideIdx, setSlideIdx] = useState(0);
  const [dataNum, setDataNum] = useState(4);
  const ref = useRef()  

  //?播放函数
  const play = () => {
    setInterval(function () {

      next();
    }, 4000);
  }
  //?自动切换函数
  const next = () => {
    if (slideIdx + 1 === dataNum) {
      setSlideIdx(0);
    } else {
      setTimeout(()=>{
        setSlideIdx(slideIdx + 1);

      },0)
    }
  };
  //?设置轮播图计时器
  useEffect(() => {
    setDataNum(imgData.length)
    play()
    console.log(1);
  }, []);

  return (
    <div className="slideWrapper">
      <div className="slideView">
        <div
          className="slideList"
          style={{ transform: `translateX(${-600 * slideIdx}px)` }}
        >
          {slideData.map((img, idx) => {
            return (
              <div key={idx}>
                <img width={600} height={390} src={img} alt="nosource" />
              </div>
            );
          })}
        </div>
      </div>
      <ul ref={ref}>
        <li ca></li>
        <li></li>
        <li></li>
        <li></li>
      </ul>
    </div>
  );
}
