    <div className="slideWrapper">
      <div className="slideView">
        <div
          className="slideList"
          style={{ transform: `translateX(${-600 * slideIdx}px)` }}
        >
          {slideData.map((img, idx) => {
            return (
              <div key={idx}>
                <img width={600} height={390} src={img} alt="nosource" />
              </div>
