        </div>
      </div>
      <ul ref={ref}>
        <li className={`${slideIdx ===0 ? "active":""}`}></li>
        <li className={`${slideIdx ===1 ? "active":""}`}></li>
        <li className={`${slideIdx ===2 ? "active":""}`}></li>
        <li className={`${slideIdx ===3 ? "active":""}`}></li>
      </ul>
    </div>
  );
}
