import React from 'react'
import { NavLink, useLocation } from 'react-router-dom'

import style from './topNav.module.css'
import title from '../../assets/img/hometitle.webp'

export default function TopNav() {
  const r = useLocation()
  const result = r.pathname.split('/')
  console.log(r);
  return (
    <div className={style.topNav}>
      <img src={title} width={"200px"} alt="" />
      <div className={style.nav}>
        <NavLink to={"/home"}>主页</NavLink>
        <NavLink to={"/characters"}>人物</NavLink>
        <NavLink to={"/wizardry"}>奇术</NavLink>
        <NavLink to={"/weapons"}>武器</NavLink>
        <NavLink to={"/dlc"}>dlc内容</NavLink>
      </div>
    </div>
  );
}
