import React, { useCallback, useRef } from "react";
import { useState } from "react";

import "./index.scss";
import slideData from "../../config/slideData";
import { useEffect } from "react";
import imgData from "../../config/slideData";

export default function SlideList() {
  const [slideIdx, setSlideIdx] = useState(0);
  const [dataNum, setDataNum] = useState(4);
  const play = useRef();

  //?自动切换函数
  const next = () => {
    if (slideIdx + 1 === dataNum) {
      setSlideIdx(0);
    } else {
      setTimeout(() => {
        setSlideIdx(slideIdx + 1);
      }, 0);
    }
  };
  //? 计时器hooks
  const useInterval = (callback, delay) => {
    //? 通过useRef()创建可变的callback以确保每次间隔运行时都有新值可用
    const savedCallback = useRef();
    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);
    useEffect(() => {
      //? 传递给setInterval()的函数将永远不会改变，但封闭的 ref 的值在调用时将始终是最新的
      function tick() {
        savedCallback.current();
      }
      let id = setInterval(tick, delay);
      return () => clearInterval(id);
    }, [delay]);
  };

  //?设置轮播图计时器
  useEffect(() => {
    useInterval(next,4000)
  }, []);
  /* useEffect(() => {
    setDataNum(imgData.length)
    play()
  }, [play]);
  useEffect(()=>{
    return ()=>{
      clearInterval(timer)
    }
  }) */
  //?点击控制轴
  const handleClick = (e) => {
    let target = parseInt(e.target.dataset.idx);
    setSlideIdx(target);
  };
  return (
    <div className="slideWrapper">
      <div className="slideView">
        <div
          className="slideList"
          style={{ transform: `translateX(${-700 * slideIdx}px)` }}
        >
          {slideData.map((img, idx) => {
            return (
              <div key={idx}>
                <img width={700} height={440} src={img} alt="nosource" />
              </div>
            );
          })}
        </div>
      </div>
      <ul>
        {slideData.map((img, idx) => {
          return (
            <li
              key={idx}
              data-idx={idx}
              onClick={handleClick}
              className={`${slideIdx === idx ? "active" : ""}`}
            ></li>
          );
        })}
      </ul>
    </div>
  );
}
