import React from "react";
import { useState } from "react";

import "./index.scss";
import slideData from "../../config/slideData";
import { useEffect } from "react";
import imgData from "../../config/slideData";

export default function SlideList() {
  let timer
  const [slideIdx, setSlideIdx] = useState(0);
  const [dataNum, setDataNum] = useState(4);
  
  //?播放函数
  const play = () => {
    timer = setInterval(function () {
      next();
    }, 4000);
  }
  //?自动切换函数
  const next = () => {
    if (slideIdx + 1 === dataNum) {
      setSlideIdx(0);
    } else {
      setTimeout(()=>{
        setSlideIdx(slideIdx + 1);
      },0)
    }
  };
  //?设置轮播图计时器
  useEffect(() => {
    setDataNum(imgData.length)
    play()
  }, [play]);
  useEffect(()=>{
    return ()=>{
      clearInterval(timer)
    }
  })
  //?点击控制轴
  const handleClick = (e) => {
    let target = parseInt(e.target.dataset.idx)
    setSlideIdx(target)
  }
  return (
    <div className="slideWrapper">
      <div className="slideView">
        <div
          className="slideList"
          style={{ transform: `translateX(${-600 * slideIdx}px)` }}
        >
          {slideData.map((img, idx) => {
            return (
              <div key={idx}>
                <img width={700} height={480} src={img} alt="nosource" />
              </div>
            );
          })}
        </div>
      </div>
      <ul>
        {slideData.map((img, idx) => {
          return (
            <li
              key={idx}
              data-idx={idx}
              onClick={handleClick}
              className={`${slideIdx === idx ? "active" : ""}`}
            ></li>
          );
        })}
      </ul>
    </div>
  );
}
