import React from "react";
import { useState } from "react";

import "./index.scss";
import slideData from "../../config/slideData";
import { useEffect } from "react";

export default function SlideList() {
  const [slideIdx, setSlideIdx] = useState(0);
  const [dataNum, setDataNum] = useState(4);
  let timer

  const next = ()=> {
    slideIdx + 1 === dataNum ? setSlideIdx(0) : setSlideIdx(slideIdx + 1);
  }
  //?设置轮播图计时器
  useEffect(() => {
    setDataNum(slideData.length)
    timer = setInterval(function() {
      console.log(slideIdx);
      next()
    },1000)
    
  },[]);

  //?清除计时器
  useEffect(()=> {
    return () => {
      clearInterval(timer)
    };
  })

  return (
    <>
      <div className="slideView">
        <div
          className="slideList"
          style={{ transform: `translateX(${-600 * slideIdx}px)` }}
        >
          {slideData.map((img, idx) => {
            return (
              <div key={idx}>
                <img width={600} height={390} src={img} alt="nosource" />
              </div>
            );
          })}
        </div>
      </div>

      <div>{}</div>
    </>
  );
}
